                onFail(FileError.INVALID_MODIFICATION_ERR);
            } else {
                entry.nativeEntry.copyTo(destEntry.nativeEntry, fileName, function () {
                    resolve(function (copiedDir) {
                        copiedEntry = copiedDir;
